<style>
  #knob {
    background-size: 100%;
    position: absolute;
    width: 60px;
    height: 60px;
    background-repeat: no-repeat;
  }
  #knob.open {
    background-image: url(data:image/png;base64,);
    right: 25px;
    top: 200px;
    transform: rotate(13deg);
    -webkit-transform: rotate(13deg);
    -moz-transform: rotate(13deg);
    -o-transform: rotate(13deg);
    -ms-transform: rotate(13deg);
  }
  #knob.closed {
    background-image: url(data:image/png;base64,);
    right: 20px;
    top: 200px;
    transform: rotate(-14deg);
    -webkit-transform: rotate(-14deg);
    -moz-transform: rotate(-14deg);
    -o-transform: rotate(-14deg);
    -ms-transform: rotate(-14deg);
  }
  #tv-block {
    width: 450px;
    height: 400px;
    background-size: 100%;
    position: relative;
    background-repeat: no-repeat;
  }
  #tv-block #tv {
    position: absolute;
    left: 0px;
    top: 0px;
  }
  #tv-block #image {
    position: absolute;
    left: 35px;
    top: 150px;
  }
</style>
<script>
  Element.prototype.hasClass = function (name) {
    var classes = this.className.split(" "),
      hasClass = false;
    for (var i = 0; i < classes.length; i++) {
      if (classes[i] === name) {
        return true;
      }
    }
    return false;
  }
  Element.prototype.removeClass = function (name) {
    if (this.hasClass(name)) {
      this.className = this.className.replace(name, '');
      return true;
    } else {
      return false;
    }
  }
  Element.prototype.addClass = function (name) {
    if (!this.hasClass(name)) {
      this.className += ' ' + name;
      return true;
    } else {
      return false;
    }
  }
  /***************************************
  * Generic AJAX handler for DRYness
  ***************************************/
  function sendAJAX(url, method, callback, data, headers) {
    try {
      var xhReq = new XMLHttpRequest(),
        xhTimeout = setTimeout(function () {xhReq.abort();}, 45000);
      if (method !== "POST" && method !== "GET" && method !== "PUT" && method !== "DELETE") {
        return false;
      }
      xhReq.open(method, url);
      if (typeof(headers) != "undefined") {
        for (var i = 0; i < headers.headers.length; i++) {
          xhReq.setRequestHeader(headers.headers[i].type, headers.headers[i].value);
        }
      }
      xhReq.onreadystatechange = function () {
        if (xhReq.readyState != 4) return;
        clearTimeout(xhTimeout);
        callback(xhReq);
      };
      if (xhReq.readyState === 4 ) return xhReq;
      xhReq.send(data);
    } catch (ex) {
      catchError(ex);
    }
  }
  function checkStatus() {
    sendAJAX('http://localhost/bodgery/misc/checkstatus.php', 'GET', function (resp) {
      if (resp.status === 200) {
        var data = JSON.parse(resp.responseText);
        if (data && (data.status === "1")) {
          $knob.removeClass('closed`');
          $knob.addClass('open');
        } else {
          $knob.addClass('closed');
          $knob.removeClass('open');
        }
      }
    });
  }
  window.setInterval(checkStatus, 30000);
  var $openSign = {}, $closedSign = {}, $knob = {};
  document.addEventListener('DOMContentLoaded', function () {
    document.getElementById('toggle').addEventListener('click', function () {
      if ($knob.hasClass('open')) {
        $knob.removeClass('open');
        $knob.addClass('closed');
      } else {
        $knob.addClass('open');
        $knob.removeClass('closed');
      }
    });
    $knob = document.getElementById('knob');
    checkStatus();
  });
</script>

<div id="tv-block">
  <img id="image" src="http://placekitten.com/g/320/210" />
  <img id="tv" src="data:image/png;base64," />
  <div id="knob" class="open"></div>
</div>

<button class="sign" id="toggle">Toggle Knob</button>
